class Product {
  final String pname;
  final String description;
  final int price;
  final String imageUrl;

  Product(this.pname, this.description, this.price, this.imageUrl);
}

class CartItem {
  final Product product;
  int quantity = 0;

  CartItem(this.product, this.quantity);
}

class SearchPage extends StatefulWidget {
  @override
  _SearchPageState createState() => _SearchPageState();
}

class _SearchPageState extends State<SearchPage> {
  List<CartItem> cartItems = [];

  void addToCart(Product products) {
    bool itemExists = false;
    for (CartItem item in cartItems) {
      if (item.product.pname == products.pname) {
        itemExists = true;
        {
          item.quantity++;
        }
        ;
        break;
      }
    }
    if (!itemExists) {
      {
        cartItems.add(CartItem(products, 1));
      }
      ;
    }
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        backgroundColor: const Color.fromARGB(255, 7, 50, 85),
        title: Row(
          children: [
            Text(
              "TradeRIT",
              style: TextStyle(
                color: Color.fromARGB(255, 232, 58, 23),
              ),
            ),
          ],
        ),
        actions: [
          IconButton(
            onPressed: () {
              Navigator.of(context).push(MaterialPageRoute(
                  builder: (context) =>
                      SearchPage())); // Handle the "Search" button logic here
            },
            icon: Icon(
              Icons.search,
              color: Colors.white,
            ),
          ),
          IconButton(
            onPressed: () {
              Navigator.of(context).push(
                  MaterialPageRoute(builder: (context) => CartPage(cartItems)));
              // Handle the "Buy" button logic here
            },
            icon: Icon(
              Icons.shopping_cart,
              color: Colors.white,
            ),
          ),
          IconButton(
            onPressed: () {
              Navigator.of(context)
                  .push(MaterialPageRoute(builder: (context) => MyItemPage()));
              // Handle the "Profile" button logic here
            },
            icon: Icon(
              Icons.file_open,
              color: Colors.white,
            ),
          ),
        ],
      ),
      body: Column(
        children: <Widget>[
          Padding(
            padding: const EdgeInsets.all(8.0),
            child: TextField(
              decoration: InputDecoration(
                labelText: 'Search',
                hintText: 'Search items...',
                prefixIcon: Icon(Icons.search),
                border: OutlineInputBorder(
                  borderRadius: BorderRadius.circular(10.0),
                ),
              ),
            ),
          ),
          Expanded(
            child: GridView.builder(
              gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                crossAxisCount: 2,
              ),
              itemCount: products.length,
              itemBuilder: (context, index) {
                return InkWell(
                  onTap: () {
                    // Navigate to product details page when the widget is tapped
                    Navigator.push(
                      context,
                      MaterialPageRoute(
                          builder: (context) => ItemDetail(products[index])),
                    );
                  },
                  child: Card(
                    color: Color.fromARGB(255, 177, 197, 214),
                    margin: EdgeInsets.all(8.0),
                    child: Column(
                      mainAxisAlignment: MainAxisAlignment.center,
                      children: <Widget>[
                        Image.network(
                          products[index].imageUrl,
                          height: 150.0,
                        ),
                        SizedBox(height: 10.0),
                        Text(
                          products[index].pname,
                          style: TextStyle(fontSize: 18.0),
                        ),
                        SizedBox(height: 5.0),
                        Text(
                          'Rs ${products[index].price}',
                          style: TextStyle(fontSize: 16.0),
                        ),
                        SizedBox(height: 10.0),
                        ElevatedButton(
                          style: ButtonStyle(
                            backgroundColor: MaterialStateProperty.all(
                                Color.fromARGB(255, 7, 50, 85)),
                          ),
                          onPressed: () {
                            Navigator.of(context).push(MaterialPageRoute(
                                builder: (context) => CartPage(cartItems)));
                            addToCart(products[index]);
                            ScaffoldMessenger.of(context).showSnackBar(
                              SnackBar(
                                content: Text(
                                    '${products[index].pname} added to cart.'),
                              ),
                            );
                          },
                          child: Text('Add to Cart'),
                        ),
                      ],
                    ),
                  ),
                );
              },
            ),
          ),
        ],
      ),
    );
  }
}

