class CartPage extends StatelessWidget {
  final List<CartItem> cartItems;
  CartPage(this.cartItems);

  int totPrice = 0;
  int BillAmount = 0;
  void amount() {
    for (CartItem item in cartItems) {
      totPrice += item.product.price * item.quantity;
    }
    BillAmount = totPrice;
    print(totPrice);
  }

  @override
  Widget build(BuildContext context) {
    return Scaffold(
      appBar: AppBar(
        backgroundColor: const Color.fromARGB(255, 7, 50, 85),
        title: Row(
          children: [
            Text(
              "TradeRIT",
              style: TextStyle(
                color: Color.fromARGB(255, 232, 58, 23),
              ),
            ),
          ],
        ),
        actions: [
          IconButton(
            onPressed: () {
              Navigator.of(context).push(MaterialPageRoute(
                  builder: (context) =>
                      SearchPage())); // Handle the "Search" button logic here
            },
            icon: Icon(
              Icons.search,
              color: Colors.white,
            ),
          ),
          IconButton(
            onPressed: () {
              Navigator.of(context).push(
                  MaterialPageRoute(builder: (context) => CartPage(cartItems)));
              // Handle the "Buy" button logic here
            },
            icon: Icon(
              Icons.shopping_cart,
              color: Colors.white,
            ),
          ),
          IconButton(
            onPressed: () {
              Navigator.of(context)
                  .push(MaterialPageRoute(builder: (context) => MyItemPage()));
              // Handle the "Profile" button logic here
            },
            icon: Icon(
              Icons.file_open,
              color: Colors.white,
            ),
          ),
        ],
      ),
      body: Column(
        children: <Widget>[
          Padding(
            padding: EdgeInsets.all(16.0),
            child: Text(
              'Cart',
              style: TextStyle(fontSize: 24.0, fontWeight: FontWeight.bold),
            ),
          ),
          Expanded(
            child: GridView.builder(
              gridDelegate: SliverGridDelegateWithFixedCrossAxisCount(
                crossAxisCount: 2,
              ),
              itemCount: cartItems.length,
              itemBuilder: (context, index) {
                return Card(
                  color: Color.fromARGB(255, 177, 197, 214),
                  margin: EdgeInsets.all(8.0),
                  child: Column(
                    mainAxisAlignment: MainAxisAlignment.center,
                    children: <Widget>[
                      Image.network(
                        cartItems[index].product.imageUrl,
                        height: 150.0,
                      ),
                      SizedBox(height: 10.0),
                      Text(
                        cartItems[index].product.pname,
                        style: TextStyle(fontSize: 18.0),
                      ),
                      SizedBox(height: 5.0),
                      Text(
                        'Rs ${cartItems[index].product.price}',
                        style: TextStyle(fontSize: 16.0),
                      ),
                      SizedBox(height: 5.0),
                      Text(
                        'Quantity: ${cartItems[index].quantity}',
                        style: TextStyle(fontSize: 16.0),
                      ),
                    ],
                  ),
                );
              },
            ),
          ),
          Expanded(
            child: Container(
              color: Color.fromARGB(255, 198, 199, 200),
              child: Column(
                children: <Widget>[
                  Padding(
                    padding: EdgeInsets.all(8.0),
                    child: Text(
                      'Bill',
                      style: TextStyle(
                          fontSize: 24.0, fontWeight: FontWeight.bold),
                    ),
                  ),
                  Expanded(
                    child: ListView.builder(
                      itemCount: cartItems.length,
                      itemBuilder: (context, index) {
                        return ListTile(
                          title: Text(cartItems[index].product.pname),
                          subtitle:
                              Text('Quantity: ${cartItems[index].quantity}'),
                        );
                      },
                    ),
                  ),
                  ElevatedButton(
                    style: ButtonStyle(
                      backgroundColor: MaterialStateProperty.all(
                          Color.fromARGB(255, 7, 50, 85)),
                    ),
                    onPressed: () {
                      return amount();
                    },
                    child: Text("Amount"),
                  ),
                  SizedBox(height: 8.0),
                  ElevatedButton(
                    style: ButtonStyle(
                      backgroundColor: MaterialStateProperty.all(
                          Color.fromARGB(255, 7, 50, 85)),
                    ),
                    onPressed: () {
                      showDialog(
                        context: context,
                        builder: (BuildContext context) {
                          return AlertDialog(
                            title: Text('Bill= Rs $BillAmount'),
                            content: Text('Proceed to Pay'),
                            actions: <Widget>[
                              TextButton(
                                onPressed: () {
                                  Navigator.of(context)
                                      .pop(); // Close the dialog
                                },
                                child: Text('PAY'),
                              ),
                            ],
                          );
                        },
                      );
                    },
                    child: Text("Generate Bill"),
                  ),
                  SizedBox(height: 8.0),
                ],
              ),
            ),
          ),
        ],
      ),
    );
  }
}

